// This component is used to create summary grocery planner list (with javascripts) from recipes stored in state
import React from 'react';
const { useState , useEffect } = React;
import { Typography, Card, Table, Image, Space } from 'antd';
const { Meta } = Card;
import { StarOutlined, SmileOutlined, HeartOutlined, SearchOutlined, PlayCircleOutlined, MinusCircleOutlined } from '@ant-design/icons';

const{ Title, Text, Link} = Typography;

const icons = [];
icons["main"] = <StarOutlined />;
icons["side"] = <SmileOutlined />;
icons["desert"] = <HeartOutlined />;
icons["other"] = <SearchOutlined />;

//https://codesandbox.io/s/responsive-sample-forked-yji5do?file=/index.js:1034-1177
//https://albertwalicki.com/learn/solutions/how-to-limit-text-to-n-lines
const columns = [
    {
        title: 'Ingredient',
        dataIndex: 'ingredientName',
        key: 'ingredientName',
        
    },
    {
        title: 'Type',
        dataIndex: 'ingredientType',
        key: 'ingredientType',
        responsive: ['md'],
    },
    {
        title: 'Description',
        dataIndex: 'ingredientDesc',
        key: 'ingredientDesc',
        responsive: ['md'],
    },
    {
        title: 'Quantity',
        dataIndex: 'quantity',
        key: 'quantity',
    }
]


const GroceryPlanner = props=>{    
    // To group ingredients by their name, quantity and unit using reduce(), 
    // then combines the quantities for each group
    const quantitiesByIngredientUnit = props.recipePlannerList.reduce((result, obj) => {
        obj.ingredient.forEach(({key, ingredientName, ingredientType, ingredientDesc, quantity, unit}) => {
            const index = result.findIndex(x => x.key === key && x.unit === unit);
            if (index === -1) {
                result.push({ingredientName, ingredientType, ingredientDesc, key:key, quantity:parseFloat(quantity), unit: unit});
            } else {
                result[index].quantity+= parseFloat(quantity);
            }
        });
        return result;
      }, []);

    // Format the quantities as desired
    const quantitiesByIngredient = {};
    quantitiesByIngredientUnit.forEach(({ key, ingredientName, ingredientType, ingredientDesc, quantity, unit }) => {
    if (!quantitiesByIngredient[key]) {
        quantitiesByIngredient[key] = {
            ingredientName, ingredientType, ingredientDesc,
            key,
            quantity: `${quantity} ${unit}`
        };
    } else {
        quantitiesByIngredient[key].quantity += ` + ${quantity} ${unit}`;
    }
    });

    const formatedTableData=Object.values(quantitiesByIngredient);


    return ( <>

        <h2>Your Grocery List</h2>

        <Table header={<div>Ingredients</div>} dataSource={formatedTableData}  columns={columns}  pagination={false} />

        <h2>Selected Recipes</h2>
        {props.recipePlannerList.length==0?<p>Please select recipes from our Recipe Index!</p>:""}

        <Space size={[8, 16]} wrap>
        {props.recipePlannerList.map(recipe=>
            <Card key={recipe._id} style={{ width: 280}} 
                cover={
                    <Image height={200}
                        src={recipe.image}
                        fallback="data:image/png;base64,"
                    />
           
                }
                actions={[
                    <PlayCircleOutlined key="nav" onClick={()=>{props.navCurrentRecipe(recipe)}}/>,
                    <MinusCircleOutlined key="remove" onClick={()=>{props.handleRemove(recipe)}} />,
                ]}>
                <Meta className="truncate" style={{ height: 80}} 
                    title={recipe.name} 
                    description={recipe.description} 
                />
            </Card>
        )}
        </Space>


    </>
    )
};
export default GroceryPlanner;